import { put, takeLatest, call, delay } from "redux-saga/effects";
import {
  dailogFetchInitated,
  dailogFetchFailure,
  dailogFetchSuccess,
} from "../../actions/sagaActions";
import axios from "axios";

function* handleDetailsFetchSaga(action) {
  try {
    yield put(dailogFetchInitated());

    const response = yield call(
      axios.get,
      `http://dummy.restapiexample.com/api/v1/employee/${action.payload}`
    );
    console.log("hi from axios", response, action.payload);
    yield delay(2000);
    yield put(dailogFetchSuccess(response.data, action.payload));
  } catch (error) {
    yield put(dailogFetchFailure());
  }
}

// eslint-disable-next-line import/prefer-default-export
export function* watchForHandleDetailFetch() {
  yield takeLatest("HANDLER_CLICKED_DETAILS", handleDetailsFetchSaga);
}
