import { all, fork } from "redux-saga/effects";

import * as employeeSagas from "./EmployeeSagas";
import * as DetailsSaga from "./DetaislSagas";

export function* rootSaga() {
  yield all([...Object.values(employeeSagas)].map(fork));
}
export function* rootSagaDetails() {
  yield all([...Object.values(DetailsSaga)].map(fork));
}
