import React, { Component } from "react";
import { connect } from "react-redux";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { withStyles } from "@material-ui/core/styles";
// import { fetchDataThunk } from "./actions/thunkActions";
import { handlerClicked, handlerClickedDetails } from "./actions/sagaActions";
import CircularProgress from "@material-ui/core/CircularProgress";
import Box from "@material-ui/core/Box";
import MUICard from "@material-ui/core/Card";
import MUICardContent from "@material-ui/core/CardContent";
import Avatar from "@material-ui/core/Avatar";
import Divider from "@material-ui/core/Divider";
import Grid from "@material-ui/core/Grid";
import Modal from "@material-ui/core/Modal";

const styles = (theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    flexGrow: 1,
  },
  spinnerRoot: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: "80vh",
  },
  card: {
    borderRadius: 12,
    minWidth: 256,
    textAlign: "center",
    backgroundColor: "#3F51B5",
    backgroundImage: "linear-gradient(315deg, #3F51B5 0%, #09203f 74%)",
  },
  Detailscard: {
    borderRadius: 12,
    height: "inherit",
    width: "inherit",
    textAlign: "center",
    backgroundColor: "#FDD7E4",
    // backgroundImage: "linear-gradient(315deg, #3F51B5 0%, #09203f 74%)",
  },
  avatar: {
    width: 60,
    height: 60,
    margin: "auto",
    backgroundColor: "#F50057",
    color: "#3F51B5",
    fontWeight: "bold",
  },
  heading: {
    fontSize: 20,
    fontWeight: "bold",
    letterSpacing: "0.5px",
    marginTop: 8,
    marginBottom: 0,
    color: "#F50057",
  },
  subheader: {
    fontSize: 18,
    color: "#c7c7c7",
    marginBottom: "0.875em",
  },
  statLabel: {
    fontSize: 14,
    color: "#c7c7c7",
    fontWeight: 500,
    fontFamily:
      '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"',
    margin: 0,
  },
  statValue: {
    fontSize: 18,
    fontWeight: "bold",
    marginBottom: 4,
    letterSpacing: "1px",
    color: "#F50057",
  },
  paper: {
    position: "absolute",
    width: theme.spacing.unit * 50,
    backgroundColor: "transparent",
    // boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    outline: "none",
  },
});
function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

class App extends Component {
  state = {
    open: false,
  };

  componentDidMount() {
    this.props.startFetchBySaga();
  }
  // handleFetchEmployeeByThunk = () => {
  //   // this.props.startFetchByThunk();
  // };

  // handleFetchEmployeeBySaga = () => {
  //   // console.log("HI from handleFetchEmployeeBySaga");
  //   this.props.startFetchBySaga();
  //   // console.log("hi after call", this.props.employees);
  // };
  handleFetchDetailsBySaga = (id) => {
    console.log(id);

    this.setState({
      open: true,
    });

    this.props.startDetailsBySaga(id);
  };
  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };
  LoadingSpinner = ({ classes }) => (
    <div className={classes.spinnerRoot}>
      <CircularProgress color="secondary" />
    </div>
  );

  EmployeeCard = ({ classes, id, login, type, repos_url, avatar_url }) => (
    <MUICard
      className={classes.card}
      onClick={() => this.handleFetchDetailsBySaga(login)}
    >
      <MUICardContent>
        <Avatar className={classes.avatar} src={avatar_url}></Avatar>
        <h3 className={classes.heading}>{login}</h3>
        <span className={classes.subheader}># {id}</span>
      </MUICardContent>
      <Divider light />
      <Box display={"flex"}>
        <Box p={2} flex={"auto"}>
          <p className={classes.statLabel}>type</p>
          <p className={classes.statValue}>{type}</p>
        </Box>
        <Box p={2} flex={"auto"}>
          <p className={classes.statLabel}>repos_url</p>
          <p className={classes.statValue}>{repos_url}</p>
        </Box>
      </Box>
    </MUICard>
  );

  DetailsCard = ({
    classes,
    id,
    employee_name,
    employee_salary,
    employee_age,
    profile_image,
  }) => (
    <MUICard className={classes.Detailscard} onClick={this.handleClose}>
      <MUICardContent>
        <Box
          style={{
            border: "solid",
            borderRadius: 12,
            color: "#0000A0",
          }}
        >
          <div>
            <h3>{employee_name}</h3>
            <h3> Id: {id}</h3>
          </div>
          <h3>Aged {employee_age} years</h3>
          <h3> Earns ${employee_salary} per annum.</h3>
        </Box>
      </MUICardContent>
    </MUICard>
  );
  render() {
    const { status, classes } = this.props;
    const { employees } = this.props;
    // const { details } = this.props;

    return (
      <>
        <div className={classes.root}>
          <AppBar position="static">
            <Toolbar>
              <Typography variant="h6" className={classes.title}>
                Employee Management
              </Typography>
              <Button
                variant="contained"
                color="secondary"
                // onClick={this.handleFetchEmployeeBySaga}
              >
                Fetch Employees
              </Button>
            </Toolbar>
          </AppBar>
          {status === "loading" ? (
            <this.LoadingSpinner classes={classes}></this.LoadingSpinner>
          ) : (
            ""
          )}
          <Grid container spacing={2} style={{ marginTop: 20 }}>
            {console.log(employees)}
            {employees.map((employee, i) => (
              <Grid item xs={12} sm={12} md={4} lg={3} key={i}>
                <this.EmployeeCard classes={classes} {...employee} />
              </Grid>
            ))}
          </Grid>

          <Modal
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
            open={this.state.open}
            onClose={this.handleClose}
          >
            {this.props.statusDetails === "loading" ? (
              <this.LoadingSpinner classes={classes}></this.LoadingSpinner>
            ) : (
              <div style={getModalStyle()} className={classes.paper}>
                <this.DetailsCard classes={classes} {...this.props.details} />
              </div>
            )}
          </Modal>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  status: state.employeeReducer.status,
  employees: state.employeeReducer.data,
  details: state.DetailsReducer.data,
  statusDetails: state.DetailsReducer.status,
});

const mapDispatchToProps = (dispatch) => ({
  // startFetchByThunk: () => dispatch(fetchDataThunk()),
  startFetchBySaga: () => dispatch(handlerClicked()),
  startDetailsBySaga: (login) => dispatch(handlerClickedDetails(login)),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles, { withTheme: true })(App));
