import React, { Component } from "react";
import { connect } from "react-redux";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { withStyles } from "@material-ui/core/styles";
// import { fetchDataThunk } from "./actions/thunkActions";
import { handlerClicked, handlerClickedDetails } from "./actions/sagaActions";
import CircularProgress from "@material-ui/core/CircularProgress";
import Box from "@material-ui/core/Box";
import MUICard from "@material-ui/core/Card";
import MUICardContent from "@material-ui/core/CardContent";
import Avatar from "@material-ui/core/Avatar";
// import Divider from "@material-ui/core/Divider";
import Grid from "@material-ui/core/Grid";
import Modal from "@material-ui/core/Modal";
import { Tooltip } from "@material-ui/core";

const styles = (theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: "#aecad6", //"#7fcec5",
    backgroundImage: "linear-gradient(315deg, #aecad6 0%, #b8d3fe 74%)", //" linear-gradient(315deg, #7fcec5 0%, #14557b 74%)",
    //backgroundColor: "#eec0c6",
    // backgroundImage: "linear-gradient(315deg, #eec0c6 0%, #7ee8fa 74%)",
    padding: "2vw",
  },
  title: {
    flexGrow: 1,
  },
  spinnerRoot: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: "80vh",
  },
  card: {
    borderRadius: 15,
    borderStyle: "solid",
    borderColor: "white",

    textAlign: "center",
    cursor: "pointer",
    backgroundColor: "#0cbaba", // "#eec0c6",
    backgroundImage: "linear-gradient(315deg, #0cbaba 0%, #380036 95%)",
    boxShadow: "0 0 10px rgba(0,0,0,.5)",
    transition: "all .5s",
    "&:hover": {
      boxShadow: " 0 8px 8px rgba(0,0,0,.25)",
      margin: "5px",
    },
  },
  Detailscard: {
    borderRadius: 12,
    height: "inherit",
    width: "inherit",
    textAlign: "center",
    backgroundColor: "#FDD7E4",
  },
  avatar: {
    width: "inherit",
    height: "inherit",
  },
  heading: {
    fontSize: 20,
    fontWeight: "bold",
    fontFamily:
      '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"',
    letterSpacing: "0.5px",
    marginTop: 8,
    marginBottom: 0,
    color: "white",
  },
  subheader: {
    fontSize: 18,
    color: "#c7c7c7",
    marginBottom: "0.875em",
  },
  statLabel: {
    fontSize: 14,
    color: "#c7c7c7",
    fontWeight: 500,
    fontFamily:
      '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"',
    margin: 0,
  },
  fetchTypo: {
    fontSize: 12,
    fontWeight: "bold",
    letterSpacing: "1px",
    color: "#FFF",
  },
  paper: {
    position: "absolute",
    width: theme.spacing.unit * 50,
    backgroundColor: "transparent",
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    outline: "none",
  },
  toolTip: {
    maxWidth: "23vw",
  },
});
function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

class App extends Component {
  state = {
    open: false,
    searchTerm: "",
  };

  componentDidMount() {
    this.props.startFetchBySaga();
  }

  handleFetchDetailsBySaga = (id) => {
    console.log(id);

    this.setState({
      open: true,
    });

    this.props.startDetailsBySaga(id);
  };
  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  editSearchTerm = (e) => {
    this.setState({ searchTerm: e.target.value });
  };

  dynamicSearch = () => {
    console.log(this.props.login);
    this.props.login.filter((name) =>
      name.toLowerCase().includes(this.state.searchTerm.toLowerCase())
    );
  };

  LoadingSpinner = ({ classes }) => (
    <div className={classes.spinnerRoot}>
      <CircularProgress color="primary" />
    </div>
  );

  EmployeeCard = ({ classes, id, login, type, repos_url, avatar_url }) => (
    <MUICard
      className={classes.card}
      onClick={() => this.handleFetchDetailsBySaga(login)}
    >
      <MUICardContent>
        <Avatar className={classes.avatar} src={avatar_url} />
        <Tooltip title={`User Name: ${login}`} className={classes.toolTip}>
          <Typography variant="h6" className={classes.heading}>
            {login}
          </Typography>
        </Tooltip>
      </MUICardContent>
    </MUICard>
  );

  DetailsCard = ({ classes, name, location, followers, avatar_url }) => (
    <MUICard className={classes.Detailscard} onClick={this.handleClose}>
      <MUICardContent>
        <Box
          style={{
            border: "solid",
            borderRadius: 12,
            color: "#0000A0",
          }}
        >
          <MUICardContent>
            <Avatar className={classes.avatar} src={avatar_url}></Avatar>
            <h3 className={classes.heading}>{name}</h3>
            <span className={classes.subheader}># {location}</span>
          </MUICardContent>
          <h3>Location {location} years</h3>
          <h3> Followers {followers} </h3>
        </Box>
      </MUICardContent>
    </MUICard>
  );
  render() {
    const { status, classes } = this.props;
    const { employees } = this.props;
    // const { details } = this.props;

    return (
      <>
        <div className={classes.root}>
          <AppBar position="static">
            <Toolbar>
              <Typography variant="h6" className={classes.title}>
                ALL USERS
              </Typography>
              <Typography variant="h6" className={classes.title}>
                <input
                  type="text"
                  value={this.state.searchTerm}
                  onChange={this.editSearchTerm}
                  placeholder="Search"
                />
              </Typography>
              <Button
                style={{ backgroundColor: "#380036" }}
                variant="contained"

                // onClick={this.handleFetchEmployeeBySaga}
              >
                <Typography className={classes.fetchTypo}>
                  Fetch Employees
                </Typography>
              </Button>
            </Toolbar>
          </AppBar>
          {status === "loading" ? (
            <this.LoadingSpinner classes={classes}></this.LoadingSpinner>
          ) : (
            ""
          )}
          <Grid container spacing={2} style={{ marginTop: 20 }}>
            {
              // console.log(employees)
            }
            {this.dynamicSearch() === null &&
              employees.map((employee, i) => (
                <Grid item xs={12} sm={12} md={4} lg={3} key={i}>
                  <this.EmployeeCard classes={classes} {...employee} />
                </Grid>
              ))}
            {this.dynamicSearch() &&
              this.dynamicSearch().map((employee, i) => (
                <Grid item xs={12} sm={12} md={4} lg={3} key={i}>
                  <this.EmployeeCard classes={classes} {...employee} />
                </Grid>
              ))}
          </Grid>

          <Modal
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
            open={this.state.open}
            onClose={this.handleClose}
          >
            {this.props.statusDetails === "loading" ? (
              <this.LoadingSpinner classes={classes}></this.LoadingSpinner>
            ) : (
              <div style={getModalStyle()} className={classes.paper}>
                <this.DetailsCard classes={classes} {...this.props.details} />
              </div>
            )}
          </Modal>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  status: state.employeeReducer.status,
  employees: state.employeeReducer.data,
  details: state.DetailsReducer.data,
  statusDetails: state.DetailsReducer.status,
});

const mapDispatchToProps = (dispatch) => ({
  // startFetchByThunk: () => dispatch(fetchDataThunk()),
  startFetchBySaga: () => dispatch(handlerClicked()),
  startDetailsBySaga: (login) => dispatch(handlerClickedDetails(login)),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles, { withTheme: true })(App));
